mapper.select:
select *
from vote
where type = #{type} and voter_status = #{voter_status} limit #{indexpages},#{pagesize}

select count(1)
from vote
where type = #{type} and voter_status = #{voter_status}

select *
from vote
where type = #{type} and voter_status = #{voter_status}

    and title like concat('%',#{title},'%') limit #{indexpages},#{pagesize}


    and open_voter_name like concat('%',#{voterName},'%') limit #{indexpages},#{pagesize}
